mapper.select:
SELECT
COUNT( CASE WHEN t.TRANSACTION_TIME > STR_TO_DATE(#{date}, '%y-%m-%d') AND t.TRANSACTION_TIME < STR_TO_DATE(CONCAT(#{date}, ' 23:59:59'), '%y-%m-%d %h:%i:%s') THEN o.ORDER_ID ELSE NULL END ) AS #{date}
FROM `ORDER` o LEFT JOIN `TRANSACTIONS` t ON t.ORDER_ID = o.ORDER_ID WHERE o.STATUS >= 30 AND t.STATUS = 1

SELECT
COUNT( CASE WHEN t.TRANSACTION_TIME > STR_TO_DATE(#{date}, '%y-%m-%d') AND t.TRANSACTION_TIME < STR_TO_DATE(CONCAT(#{date}, ' 23:59:59'), '%y-%m-%d %h:%i:%s') THEN ci.COMMERCE_ITEM_ID ELSE NULL END ) AS #{date}
FROM `ORDER` o LEFT JOIN COMMERCE_ITEM ci ON o.ORDER_ID = ci.ORDER_ID LEFT JOIN `TRANSACTIONS` t ON t.ORDER_ID = o.ORDER_ID WHERE o.STATUS >= 30 AND t.STATUS = 1

SELECT
IFNULL(SUM( CASE WHEN t.TRANSACTION_TIME > STR_TO_DATE(#{date}, '%y-%m-%d') AND t.TRANSACTION_TIME < STR_TO_DATE(CONCAT(#{date}, ' 23:59:59'), '%y-%m-%d %h:%i:%s') THEN t.AMOUNT ELSE 0 END ), 0) AS #{date}
FROM TRANSACTIONS t WHERE `STATUS` = 1
