mapper.update:
UPDATE `ORDER`
status = #{status}, shipping_fee = #{shippingFee}, subtotal = #{subtotal}, total = #{total}, user_comments = #{userComments}, SUBMIT_DATE = #{submitDate}, update_date = now()
WHERE order_id = #{id}

update.foreach:
UPDATE COMMERCE_ITEM
order_id = #{item.orderId}, reference_id = #{item.referenceId}, name = #{item.name}, quality = #{quality}, list_price = #{item.listPrice}, sale_price = #{item.salePrice}, quantity = #{item.quantity}, amount = #{item.amount}, snapshot_id = #{item.snapshotId}, `index` = #{item.index}, update_date = now()
WHERE commerce_item_id = #{item.id}

mapper.delete:
DELETE FROM COMMERCE_ITEM
WHERE commerce_item_id IN

	#{item}
