mapper.update:
UPDATE `ORDER`
`STATUS` = #{status}, SHIPPING_FEE = #{shippingFee}, SUBTOTAL = #{subtotal}, TOTAL = #{total}, USER_COMMENTS = #{userComments}, `TYPE` = #{type}, SUBMIT_DATE = #{submitDate}, UPDATE_DATE = now()
WHERE ORDER_ID = #{id}

update.foreach:
UPDATE COMMERCE_ITEM
ORDER_ID = #{item.orderId}, REFERENCE_ID = #{item.referenceId}, `NAME` = #{item.name}, QUALITY = #{item.quality}, LIST_PRICE = #{item.listPrice}, SALE_PRICE = #{item.salePrice}, QUANTITY = #{item.quantity}, AMOUNT = #{item.amount}, SNAPSHOT_ID = #{item.snapshotId}, MERCHANT = #{item.merchant}, `INDEX` = #{item.index}, UPDATE_DATE = now(), `TYPE` = #{item.type}
WHERE COMMERCE_ITEM_ID = #{item.id}

mapper.delete:
DELETE FROM COMMERCE_ITEM
WHERE commerce_item_id IN

	#{item}
